import React, { useState, useEffect } from 'react';
import axios from 'axios';

const MoodTracker = () => {
  const [mood, setMood] = useState('');
  const [date, setDate] = useState(new Date().toISOString().slice(0, 10)); // Date actuelle au format YYYY-MM-DD

  const handleMoodChange = (event) => {
    setMood(event.target.value);
  };

  const handleSubmit = async () => {
    try {
      await axios.post('http://localhost:3001/api/moods', {
        Date: date,
        Moods: mood,
      });
      alert('Humeur mise à jour avec succès');
    } catch (error) {
      console.error('Erreur lors de la mise à jour de l\'humeur', error);
      alert('Erreur lors de la mise à jour de l\'humeur');
    }
  };

  return (
    <div>
      <h2>Suivi de l'humeur</h2>
      <label>Date:</label>
      <input
        type="date"
        value={date}
        onChange={(e) => setDate(e.target.value)}
      />
      <br />
      <label>Choisissez votre humeur:</label>
      <select value={mood} onChange={handleMoodChange}>
        <option value="">Sélectionnez une humeur</option>
        <option value="Happy">Heureux</option>
        <option value="Sad">Triste</option>
        <option value="Neutral">Neutre</option>
        {/* Ajoutez d'autres options ici */}
      </select>
      <br />
      <button onClick={handleSubmit}>Valider</button>
    </div>
  );
};

export default MoodTracker;
