import React, { useState, useEffect } from 'react';
import './PunctualTask.css';

const PunctualTask = () => {
  const [tasks, setTasks] = useState([]);

  useEffect(() => {
    fetch('http://localhost:3001/tasks')
      .then((response) => response.json())
      .then((data) => {
        setTasks(data || []); // On suppose que les tâches ponctuelles sont dans un tableau
      })
      .catch((error) => console.error('Erreur de chargement des tâches ponctuelles :', error));
  }, []);

  const handleTaskCompletion = (task) => {
    const updatedTasks = tasks.filter((t) => t.text !== task.text); // Suppression de la tâche complétée
    setTasks(updatedTasks);

    // Met à jour le fichier punctualTasks.json avec les tâches restantes
    fetch('http://localhost:3001/tasks', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(updatedTasks),
    })
    .then(response => {
      if (!response.ok) {
        throw new Error('Failed to update tasks');
      }
      return response.text();
    })
    .then(data => console.log('Mise à jour réussie :', data))
    .catch((error) => console.error('Erreur lors de la mise à jour du fichier JSON :', error));

    // Enregistre la tâche complétée dans extraTaskTracker.json
    const currentDate = new Date().toISOString().split('T')[0];
    fetch('http://localhost:3001/api/extra-tasks', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({
        Date: currentDate,
        ExtraTasks: [task], // Ajoute la tâche accomplie
        importance: 'moyen', // Exemple d'importance par défaut (peut être modifié)
      }),
    })
    .then(response => {
      if (!response.ok) {
        throw new Error('Failed to log completed task');
      }
      return response.text();
    })
    .then(data => console.log('Tâche complétée enregistrée :', data))
    .catch((error) => console.error('Erreur lors de l\'enregistrement de la tâche accomplie :', error));
  };

  return (
    <div className="punctual-task neon-green">
      <h2>Punctual Tasks</h2>
      {tasks.length > 0 ? tasks.map((task, index) => (
        <div key={index} className="task-p-item">
          <input
            type="checkbox"
            onChange={() => handleTaskCompletion(task)}
          />
          {task.text}
        </div>
      )) : <p>Aucune tâche ponctuelle pour aujourd'hui</p>}
    </div>
  );
};

export default PunctualTask;
